mapper.select:
SELECT SUMSCORE
FROM PLACE
WHERE ID = #{placeId}

SELECT NUMSCORE
FROM PLACE
WHERE ID = #{placeId}

SELECT SUMSCORE
FROM PLACE
WHERE ID = #{placeId}

SELECT NUMSCORE
FROM PLACE
WHERE ID = #{placeId}

mapper.update:
UPDATE PLACE
SET
	SUMSCORE = #{new_sum_score},
	NUMSCORE = #{new_num_score}
WHERE ID = #{placeId}
