mapper.select:
SELECT SUMSCORE
FROM RESTAURANT
WHERE ID = #{restaurantId}

SELECT NUMSCORE
FROM RESTAURANT
WHERE ID = #{restaurantId}

SELECT TAG
FROM RESTAURANT
WHERE ID = #{restaurantId}

mapper.update:
UPDATE RESTAURANT
SET
	SUMSCORE = #{new_sum_score},
	NUMSCORE = #{new_num_score}
WHERE ID = #{restaurantId}
